mapper.select:
select * from award_examine


        and achievement_name like '%${achievement_name}%'


        and leader_id = #{leader_id}


        and award_name = #{award_name}



        and award.award_time >= #{award_time_start}
        and award.award_time <= #{award_time_end}



        and issuing_authority like '%${issuing_authority}%'


        and approval_number like '%${approval_number}%'


        and aod_id = #{aod_id}


        and dr_id =#{dr_id}


        and ar_id =#{ar_id}


        and al_id = #{al_id}


        and sc_id =#{sc_id}


        and subject_id =#{subject_id}


        and sd_id =#{sd_id}


        and at_id =#{at_id}


        and examine_status =#{examine_status}



        and award.apply_time >= #{apply_time_start}
        and award.apply_time <= #{apply_time_end}



        and reviewer_id =#{reviewer_id}




    limit #{key_offset},#{pageSize}

mapper.delete:
delete from award_examine
where
leader_id = #{leader_id}
and
award_name = #{award_name}

mapper.select:
select * from award_examine


        and achievement_name like '%${achievement_name}%'


        and leader_id = #{leader_id}


        and award_name = #{award_name}



        and award.award_time >= #{award_time_start}
        and award.award_time <= #{award_time_end}



        and issuing_authority like '%${issuing_authority}%'


        and approval_number like '%${approval_number}%'


        and aod_id = #{aod_id}


        and dr_id =#{dr_id}


        and ar_id =#{ar_id}


        and al_id = #{al_id}


        and sc_id =#{sc_id}


        and subject_id =#{subject_id}


        and sd_id =#{sd_id}


        and at_id =#{at_id}


        and examine_status =#{examine_status}



        and award.apply_time >= #{apply_time_start}
        and award.apply_time <= #{apply_time_end}



        and reviewer_id =#{reviewer_id}

select ae_id from award_examine
where
award_name = #{award_name}
and
leader_id = #{leader_id}
