mapper.select:
select * from Patent
where
application_number = #{application_number}
and
public_number = #{public_number}
and
authorization_number = #{authorization_number}

select * from patent


        and patent_name = #{patent_name}


        and pt_id = #{pt_id}


        and pr_id = #{pr_id}


        and ps_id = #{ps_id}


        and aod_id = #{aod_id}


        and application_number = #{application_number}



        and project.application_time >= #{application_time_start}
        and project.application_time <= #{application_time_end}



        and public_number =#{public_number}



        and project.public_time >= #{public_time_start}
        and project.public_time <= #{public_time_end}



        and authorization_number =#{authorization_number}



        and project.authorization_time >= #{authorization_time_start}
        and project.authorization_time <= #{authorization_time_end}





    limit #{key_offset},#{pageSize}
