mapper.update:
update user
set
user_pwd=#{user_pwd}
where
user_act=#{user_act}
and
user_email=#{user_email}
and
user_number=#{user_number}
and
user_id_number=#{user_id_number}

update user
set
user_state=#{user_state}
where user_id=#{user_id}

mapper.select:
select user_id from user
where
user_email = #{user_email}

mapper.update:
update user set
role_id = #{role_id}
where
user_id = #{user_id}
