mapper.select:
select * from book_examine


        and book_name like '%${book_name}%'


        and leader_id = #{leader_id}


        and press like '%${press}%'


        and bt_id = #{bt_id}


        and pp_id = #{pp_id}


        and isbn = #{isbn}


        and word_number like '%${word_number}%'



        and public_number like '%${public_number}%'


        and trans =#{trans}


        and sc_id =#{sc_id}


        and subject_id =#{subject_id}



        and book.publish_time >= #{publish_time_start}
        and book.publish_time <= #{publish_time_end}



        and aod_id =#{aod_id}


        and sd_id =#{sd_id}


        and rt_id =#{rt_id}


        and examine_status =#{examine_status}



        and award.apply_time >= #{apply_time_start}
        and award.apply_time <= #{apply_time_end}



        and reviewer_id =#{reviewer_id}




    limit #{key_offset},#{pageSize}

mapper.delete:
delete from book_examine
where
leader_id = #{leader_id}
and
book_name =#{book_name}

mapper.select:
select count(*) total from book_examine


        and book_name like '%${book_name}%'


        and leader_id = #{leader_id}


        and press like '%${press}%'


        and bt_id = #{bt_id}


        and pp_id = #{pp_id}


        and isbn = #{isbn}


        and word_number like '%${word_number}%'



        and public_number like '%${public_number}%'


        and trans =#{trans}


        and sc_id =#{sc_id}


        and subject_id =#{subject_id}



        and book.publish_time >= #{publish_time_start}
        and book.publish_time <= #{publish_time_end}



        and aod_id =#{aod_id}


        and sd_id =#{sd_id}


        and rt_id =#{rt_id}


        and examine_status =#{examine_status}



        and award.apply_time >= #{apply_time_start}
        and award.apply_time <= #{apply_time_end}



        and reviewer_id =#{reviewer_id}




    limit #{key_offset},#{pageSize}

select be_id from book_examine
where
book_name = #{book_name}
and
leader_id = #{leader_id}
